import Image from "next/image";
import { memo } from "react";

const NextImageFull: React.FC<{
  alt?: string;
  src?: string;
}> = memo(({ alt, src }) => {
  return (
    <Image
      alt={alt}
      src={src}
      // layout="fill"
      fill
      className={"image"}
      objectFit="cover"
      placeholder="blur"
      // blurDataURL={`data:image/svg+xml;base64,${toBase64(shimmer(700, 475))}`}
      blurDataURL="data:image/jpeg;base64,"
    />
  );
});

export default NextImageFull;
